(176, 1, '2019-10-14 07:45:45', '2019-10-14 07:45:45', '', '50', '', 'inherit', 'open', 'closed', '', '50', '', '', '2019-10-14 07:45:45', '2019-10-14 07:45:45', '', 174, 'http://localhost/seagamefootball/wp-content/uploads/2019/10/50.jpg', 0, 'attachment', 'image/jpeg', 0),
